INSERT INTO `pages` (`id`, `view`, `cat`, `podcat`, `menu_ru`, `menu_ua`, `menu_en`, `title_ru`, `title_ua`, `title_en`, `descript_ru`, `descript_ua`, `descript_en`, `headtitle_ru`, `headtitle_ua`, `headtitle_en`, `headdecript_ru`, `headdecript_ua`, `headdecript_en`, `intro_ru`, `intro_ua`, `intro_en`, `text_ru`, `text_ua`, `text_en`, `page`, `public`) VALUES
(8, 'main', '', '', 'Главная', 'Головна', 'Home', 'Зерновой Терминал', 'Зерновий Термінал', 'Grain Terminal', '«УкрТрансАгро» - это современнейший в Азовской акватории Украины производственный комплекс. С 2008 года мы оказываем полный спектр услуг сертифицированного зернового терминала: приемка, накопление, хранение, сопровождение и отгрузка на морские суда зерновых и масличных культур, и другие услуги сертифицированного зернового терминала.', 'нет', 'UkrTransAgro, LLC is a modern grain transshipment complex in Ukrainian water area of the Sea of Azov. The Company offers a range of services for acceptance, accumulation, storage and loading of sea vessels with grains and oil-bearing crops, and provides other services as a grain terminal.', 'Зерновой Терминал', 'Зерновий Термінал', 'Grain Terminal', 'ООО \"УкрТрансАгро\" – зерноперевалочный комплекс в акватории Азовского моря (г. Мариуполь, Украина). ', 'ТОВ «УкрТрансАгро» - зерноперевалочний комплекс в акваторії  Азовського моря (м.Маріуполь, Україна). ', 'UkrTransAgro, LLC grain transfer complex in the Sea of Azov (Mariupol, Ukraine). ', '«УкрТрансАгро»  - это современнейший в украинской акватории Азовского моря зерноперевалочный комплекс. Мы предоставляем услуги по приемке, накоплению, хранению и отгрузке на морские суда зерновых и масличных культур, а также другие услуги  зернового терминала.', 'ТОВ «Укртрансагро» - це найсучасніший в українській акваторії Азовського моря зерно перевалочний комплекс. Ми надаємо послуги з приймання, накопичення, зберігання і відвантаження на морські судна зернових і олійних культур, та інші послуги зернового терміналу.', 'UkrTransAgro, LLC is a modern grain transshipment complex in Ukrainian water area of the Sea of Azov. The Company offers a range of services for acceptance, accumulation, storage and loading of sea vessels with grains and oil-bearing crops, and provides other services as a grain terminal.', '', '', '', '', 'yes'),
(9, 'about', '', '', 'О нас', 'Про нас', 'About us', 'О компании', 'Про компанiю', 'About us', '«УкрТрансАгро»  - это современнейший в украинской акватории Азовского моря зерноперевалочный комплекс. Мы предоставляем услуги по приемке, накоплению, хранению и отгрузке на морские суда зерновых и масличных культур, а также другие услуги  зернового терминала.', 'нет', 'none', 'Компания', 'Компанiя', 'About company', 'Мы предоставляем полного комплекса услуг по сопровождению зерновых, масличных грузов и продуктов их переработки на всех этапах логистической цепочки от производителя до экспорта морскими судами.', 'Ми надаємо комплекс послуг з перевалки, накопиченню, зберіганню і відвантаженню на морські судна зернових і олійних культур, а також повний спектр послуг зернового терміналу.', 'We provide a range of services on transfer, accumulation, storage and shipment to seagoing vessels of grains and oilseeds, as well as full-service grain terminal.', '«УкрТрансАгро» - это современнейший в украинской акватории Азовского моря зерноперевалочный комплекс. Мы оказываем комплекс услуг по перевалке, накоплению, хранению и отгрузке на морские суда, а также экспедирование и таможенное оформление зерновых и масличных культур.', 'ТОВ «УкрТрансАгро» - це найсучасніший в українській акваторії Азовського моря зерно перевалочний комплекс. Ми надаємо послуги з приймання, накопичення, зберігання і відвантаження на морські судна зернових і олійних культур, та інші послуги зернового терміналу.', 'UkrTransAgro, LLC is a modern grain transshipment complex in Ukrainian water area of the Sea of Azov. The Company offers a range of services for acceptance, accumulation, storage and loading of sea vessels with grains and oil-bearing crops, and provides other services as a grain terminal.', '<p>Компания была основана в 2004 году для предоставления услуг по перевалке зерновых культур. В 2008 году было завершено строительство современного зернового терминала и уже осенью 2008 года было отгружено первое судно.</p>\r\n\r\n<p>С 2010 года компания находится под управлением  <a href=\"http://ukmig.com.ua\" target=\"_blank\">Управляющей компании «Мариупольская инвестиционная группа»</a>.</p>\r\n\r\n<p>В 2013 году наша компания получила статус полноправного члена EBA - Европейской бизнес-ассоциации.</p>\r\n\r\n<p>С целью минимизации затрат, приходящихся на логистику грузов клиентов, на базе «УкрТрансАгро» в январе 2015 года была основана логистическая компания <a href=\"http://uta-logistic.com\" target=\"blank\">«УТА Логистик»</a>.</p>\r\n\r\n<p>В 2015 году ООО «УкрТрансАгро» признано лидером отрасли «Складское хозяйство» в Донецкой области согласно рейтинга Союза Национальных бизнес-рейтингов за значительный вклад в развитие отрасли и экономики Украины в целом, приверженность высоким стандартам социальной защищенности работников, внедрение передовых технологий и создание имиджа Украины как правового экономически развитого государства.</p>\r\n\r\n<p>С 2018 года ООО «УкрТрансАгро» предоставляет услуги по экспедированию и таможенному оформлению зерновых, масличных грузов и продуктов их переработки.</p>', '<p>Компанія заснована в 2004 році з метою надання послуг з перевалки зернових культур. У 2008 році завершено будівництво сучасного зернового терміналу і вже восени 2008 року відвантажено перше судно.</p>\r\n\r\n<p>З 2010 року компанія знаходиться під управлінням  <a href=\"http://ukmig.com.ua/ua\" target=\"blank\">Керуюча компанія «Маріупольська інвестиційна група»</a>. </p>\r\n\r\n<p>У 2013 році наша компанія отримала статус повноправного члена EBA - Європейської бізнес-асоціації.</p>\r\n\r\n<p>З метою мінімізації витрат, що пов’язані з логістикою  вантажів клієнтів, на базі «Укртрансагро» в січні 2015 року була заснована логістична компанія <a href=\"http://uta-logistic.com\" target=\"blank\">«УТА Логістик»</a>.</p>\r\n\r\n<p>У 2015 році ТОВ «Укртрансагро» визнано лідером галузі «Складське господарство» в Донецькій області відповідно до рейтингу Союзу Національних бізнес-рейтингів за значний внесок у розвиток галузі та економіки України в цілому, прихильність високим стандартам соціальної захищеності працівників, впровадження передових технологій і створення іміджу України як правової, економічно розвиненої держави.</p>\r\n\r\n\r\n', '<p>UkrTransAgro was founded in 2004 to provide services of grain crops transshipment. In 2008, the construction of modern grain terminal was completed, and in autumn 2008 the first sea vessel was shipped out there. </p>\r\n\r\n<p>The Company has been managed by  <a href=\"http://ukmig.com.ua/en\" target=\"_blank\">Management Company Mariupol Investment Group (MC MIG) </a> since 2010.</p>\r\n\r\n<p>Our Company became a full member of the European Business Association (EBA) in 2013. </p>\r\n\r\n<p>In January 2015 the logistic company <a href=\"http://uta-logistic.com\" target=\"blank\">UTA Logistic </a> was established on the basis of UkrTransAgro with purpose to minimize the clients’ costs relating to the cargoes logistics.</p>\r\n\r\n<p>In 2015, UkrTransAgro was recognized as a storage facilities leader of Donetsk region by the Union of National Business Ratings for Company’s significant contribution to the industry, its commitment to high standards of social protection of employees, the introduction of advanced technologies, and the promotion of the image of Ukraine as a legal and economically developed country</p>', '', 'yes'),
(10, 'partners', '', '', 'Партнеры', 'Партнери', 'Partners', 'Партнеры', 'Партнери', 'Partners', '', '', '', 'Партнеры', 'Партнери', 'Partners', 'За годы успешной работы, мы зарекомендовали себя как надежного оператора на зерновом рынке и установили прочные партнерские отношения с ведущими украинскими и транснациональными компаниями.', 'За роки успішної роботи, компанія «Укртрансагро» зарекомендувала себе як надійний оператор на зерновому ринку і встановила міцні партнерські відносини з провідними українськими та транснаціональними компаніями.', 'Over the course of 10 years of successful operation UkrTransAgro proved itself to be a reliable operator in the grain market, and established steady partnership relations with leading Ukrainian and international Companies.', 'Мы дорожим каждым из наших партнеров, и уделяем каждому из них особое внимание. Соблюдение взятых на себя обязательств на основании заключенных договоров, высокое качество предоставляемых услуг и профессионализм позволяют нам ежегодно сотрудничать с действующими партнерами и привлекать новых!<br> <br>Мы постоянно стремимся к совершенству и открыты для сотрудничества. <br> <br><strong>Присоединяйтесь к числу наших партнеров и открывайте для себя новые перспективы! </strong><br> <br> <block>Преимущества сотрудничества с нами уже оценили:</block>', '', '', '<intro>\r\n\r\n<strong>Зернотрейдеры и производители:</strong><br>\r\n<br>\r\n<li><span> HarvEast </span></li>\r\n<li><span> Пологовский маслоэкстракционный завод </span></li>\r\n<li><span> ДП «Сангрант Плюс» </span></li>\r\n<li><span> OILSAADS TRADING LP</span></li>\r\n<li><span> MILLCORP GENEVE SA</span></li>\r\n<li><span> МГТ БЛЕК СИ </span></li>\r\n<li><span>ДП «Ильич-Агро Донбасс» </span></li>\r\n\r\n<strong> Наши партнеры:</strong> <br>   <br>   \r\n<li><span>Управляющая компания  «Мариупольская  инвестиционная группа»</span></li>\r\n<li><span>Азовский судоремонтный завод («СРЗ»)</span></li>\r\n<li><span>Мартлет </span></li>\r\n<li><span>Маритайм Логистикс </span></li>\r\n<li><span>Свит Агро Трейд </span></li>\r\n\r\n</intro>', 'Ми дорожимо кожним з наших партнерів, і приділяємо кожному з них особливу увагу.  Дотримання взятих на себе зобов`язань, на підставі укладених договорів, висока якість наданих послуг і професіоналізм дозволяють нам щорічно співпрацювати з діючими партнерами і залучати нових!\r\nМи постійно прагнемо до досконалості і відкриті для співпраці. Приєднуйтеся до наших партнерів і відкривайте для себе нові перспективи!', 'We value our partners, and pay special attention to each of them. \r\nThe compliance with Contract commitments, the high quality of services and the professionalism allow us to cooperate with the existing partners and attract new ones!\r\n<br>We always strive for excellence and we are open for cooperation. We comprehend that success is possible only by joint efforts. Therefore we confidently declare that our partners are our support! Please become one of our partners and you’ll discover new prospects for your business!\r\n', '', 'yes'),
(11, 'strategy', '', '', 'Стратегия', 'Стратегія', 'Strategy', 'Стратегия', 'Стратегія', 'Strategy', '', '', '', 'Стратегия', 'Стратегія', 'Strategy', 'Мир бизнеса находится в постоянном движении: меняются потребности клиентов, появляются\r\nновые конкуренты, совершенствуются технологии. Все это заставляет нас постоянно\r\nсовершенствоваться и не стоять на месте.', 'Світ бізнесу знаходиться в постійному русі: змінюються потреби клієнтів, з\'являються нові конкуренти, вдосконалюються технології, а держава частенько підносить сюрпризи, «переписуючи» правила гри.', 'The business world is in constant motion: changing customer needs, new competitors, improved technology, and the state often surprises, \"rewriting the\" rules of the game.', 'Долгосрочная стратегия развития ООО «УкрТрансАгро» предусматривает развитие технологий\r\nуправления и автоматизацию бизнес-процессов.\r\nКомпания осуществила переход на использование современной системы управления\r\nпроизводством и технологическими процессами, используя автоматизированные системы 1С 8\r\n«Управление производственным предприятием», 1С 8 «Элеватор», 1С 8 «Управление очередью».\r\nПомимо этого, совместно с подрядчиками из Киева команда предприятия разрабатывает новое\r\nпрограммное обеспечение, призванное перевести мониторинг и контроль всеми процессами на\r\nпредприятие в «единое информационное пространство», которое объединит в себе морской\r\nтерминал, автоперевозчиков, трейдеров, специалистов по фумигации и клиентов компании. Это\r\nпозволит сократить трансакционные издержки для наших клиентов, как следствие – сделает\r\nсотрудничество с нашей компанией более выгодным и устойчивым.', 'ТОВ «Укртрансагро» безперервно розвивається. Змінюються потреби Клієнтів, з`являються нові конкуренти, поліпшуються технології. Все це змушує нас постійно вдосконалюватися і не стояти на місці. За останні два роки, щоб зміцнити свої переваги і повною мірою скористатися новими можливостями для посилення наших позицій на ринку, була скоригована стратегія. Для звернення уваги до нової стратегії компанії, був проведений комплексний ребрендинг від зміни логотипу та дизайну корпоративного сайту до зміни філософії компанії в цілому. Зміни філософії компанії торкнулися багатьох моментів, зокрема, змістився фокус нашої роботи - відбулася відмова від індивідуального характеру та особистих вигод на користь консолідації зусиль і знань різних учасників ринку. Така інтеграція та співпраця розширить можливості компанії та дозволить вийти на новий рівень розвитку.', 'UkrTransAgro, LLC is evolving constantly. We  react to the new customers’ needs,  to the rise of new competitors and to the advances in technologies. All these changes make us improve incessantly and not to stand still.\r\nOver the past two years the Company’s Strategy has been adjusted to strengthen our advantages and benefit from new opportunities  in full to solidify our positions in the market.\r\nThe Company went through a comprehensive rebranding to call attention to its new strategy. We’ve changed everything starting with the design of the logo and the corporate website up to rethinking of the Company’s philosophy. Changes in Company’s philosophy affected many aspects, and in particular, shifted the focus of our activity. We’ve rejected individual ambitions and personal benefits in favor of consolidating the efforts and knowledge of different market players. We believe such integration and collaboration will enhance our Company’s business opportunities and enable a new level of development.', '', '<block>Стратегічне бачення компанії виділяється в три ключові стратегічні цілі:</block>\r\n                <ul class=\"strategy\">\r\n                    <li><span>ПІДВИЩЕННЯ ЕФЕКТИВНОСТІ УПРАВЛІННЯ</span><p>Стратегія компанії щодо підвищення ефективності управління має на меті подальшу автоматизацію внутрішніх бізнес-процесів компанії. Ми продовжуємо перехід на сучасну систему управління виробництвом і технологічними процесами, активно працюємо в даному напрямку, впроваджуючи автоматизовані системи 1С 8 «Управління виробничим підприємством», 1С 8 «Елеватор», 1С 8 «Управління чергою», 1С 8 «ТОиР». Підтримуємо культуру безперервних вдосконалень. Наша команда прагне до досягнення високого рівня ефективності на всіх рівнях організації. Це завдання передбачає фундаментальний розвиток культури організації в напрямку впровадження культури безперервних поліпшень, заснованої на філософії постійного вдосконалення.</p></li>\r\n                    <li><span>ОСВОЄННЯ НОВИХ НАПРЯМКІВ ДІЯЛЬНОСТІ</span><p>ММи прагнемо підвищити свою цінність для наших Клієнтів. Найближчим часом ми плануємо розширити спектр наданих послуг, щоб охопити ще більшу частину ринку.\r\n                            <br>Такими послугами є:\r\n                            <br> - експедиторська діяльність, <br> - трейдерська діяльність, <br> - логістична діяльність\r\n                            <br>На даний час по всім  напрямках зроблено наступну роботу:\r\n                            <br> - розроблено стратегію виходу на ринок експедиторських послуг;\r\n                            <br> - створено логістичну компанія ТОВ «УТА Логістик»;\r\n                            <br> - розроблено модель функціонування накопичувального майданчика автотранспорту для управління чергою, що дозволило збільшити кількість прийнятих автомобілів.</p>\r\n                    </li>\r\n                    <li><span>БУДІВНИЦТВО НОВИХ ПОТУЖНОСТЕЙ</span><p>Ми продовжуємо інвестувати в сучасні виробничі потужності та обладнання, щоб підтримати статус передового гравця. Стратегія спрямована на забезпечення необхідних потужностей для диверсифікації перевалки, забезпечення надійної безперебійної роботи.</p></li>\r\n                </ul>\r\n                <intro>Внедрение каждой из этих стратегических задач и сильная корпоративная культура позволяют успешно воплощать наше видение в реальность!</intro>', '<block>Our strategic vision is directed towards three key targets:</block>\r\n                <ul class=\"strategy\">\r\n                    <li><span>IMPROVEMENT OF MANAGEMENT EFFECTIVENESS</span><p>The Company’s strategy on improvement of management effectiveness is targeted at further automation of internal business processes. We continue the transition to the modern system of management and technological processes by introducing new software 1C 8: Manufacturing Enterprise Management, 1C 8: Grain Elevator, and 1C 8: Queue management, 1C 8: Maintenance and repair. We support the continuous improvement culture. Our team seeks to achieve a high level of efficiency at all levels of the organization. This task implies the fundamental development of Company’s corporate standards in direction of implementation of continuous improvement culture based on the philosophy of constant improvement process.</p></li>\r\n                    <li><span>DEVELOPMENT OF NEW DIRECTIONS FOR BUSINESS</span><p>UkrTransAgro seeks to increase its advantages for Clients. The Company plans to enlarge the range of services that will help to capture the major part of the market.\r\n<br>These services are:\r\n               <br>- forwarding activity\r\n               <br>- trading activity\r\n               <br>- logistic activity\r\nTo date, the Company has performed the following work in forwarding and trading directions:\r\n<br>- the Strategy for forwarding services market entrance was prepared;\r\n<br>- the logistics company UTA Logistic, LLC was incorporated;\r\n<br>- the new model of the vehicles accumulation area for queue management was elaborated and implemented  that allowed to increase the number of accepted vehicles </p>\r\n                   </li>\r\n                    <li><span>CONSTRUCTION OF NEW FACILITIES</span><p>      We continue investing in modern production facilities and equipment to maintain the status of leading player. The Strategy is aimed at providing the necessary facilities for the diversification of the transshipment and maintenance of reliable trouble-free operation.</p></li>\r\n                </ul>\r\n                <intro>.</intro>', '', 'yes'),
(12, 'team', '', '', 'Команда', 'Команда', 'Team', 'Команда', 'Команда', 'Team', '', '', '', 'Команда', 'Команда', 'Team', 'Наши сотрудники - наша гордость! Профессионализм наших сотрудников гарантирует высокое качество наших услуг и внимательное отношение к каждому клиенту.', 'Наші співробітники - наша гордість! Професіоналізм наших співробітників гарантує високу якість наших послуг і дбайливе ставлення до кожного клієнта', 'We take pride in our staff members! Professionality of our employees is a guarantee of high quality services  and advertency to every client', 'Мы – команда профессионалов, влюбленных в свое дело. Мы строим большие планы и объединяемся для их достижения. Мы поддерживаем каждого члена коллектива, помогая раскрыть его индивидуальность и талант. Мы постоянно развиваемся!', 'Ми - команда професіоналів, що закохані у свою справу. У нас великі плани на майбутнє та ми об`єднуємося для їх досягнення. Ми підтримуємо кожного члена колективу, допомагаючи розкрити його індивідуальність і талант. Ми постійно розвиваємося!', 'We are a team of experts  who are fond of our work. We make great plans and join together  for their achievement. We support every member of the staff assisting to reveal their personality and talent. We are constantly evolving.', '', '', '', '', 'yes'),
(13, 'docs', '', '', 'Сертификаты и дипломы', 'Сертифікати та дипломи', 'Certificates and diplomas', 'Сертификаты и дипломы', 'Сертифікати та дипломи', 'Certificates and diplomas', '', '', '', 'Сертификаты и дипломы', 'СЕРТИФІКАТИ ТА ДИПЛОМИ ', 'Certificates and diplomas ', 'Расширяя сегодня границы своей деятельности, мы, как и прежде, непреклонны в требованиях к качеству. Это один из главных принципов в работе нашей компании.', 'Розширюючи сьогодні межі своєї діяльності, ми, як і колись, непохитні у вимогах до якості. Це один з головних принципів у роботі нашої компанії.', 'Expanding the boundaries of our activity today, as before, we are uncompromising in requirements to quality. This is one of the main principles of our Company.', 'Все нижеперечисленные знаки качества, которые были нам присуждены, еще раз подтверждают, что компания «УкрТрансАгро» уделяет огромное внимание вопросам качества обслуживания. Мы не перестаем делать все для того, чтобы оставаться лучшими!', 'Всі перелічені нижче знаки якості, які були нам присуджені, ще раз підтверджують, що компанія «Укртрансагро» приділяє величезну увагу питанням якості обслуговування. Ми не перестаємо робити все для того, щоб залишатися кращими!', 'All of the following quality marks that were awarded to us, once again confirmed that the company \"Ukrtransagro\" pays great attention to the quality of service. We continue to do everything in order to be the best!', '', '', '', '', 'yes'),
(14, 'services', '', '', 'Услуги', 'Послуги', 'Services', 'Услуги', 'Послуги', 'Services', '', '', '', 'Услуги', 'Послуги', 'Services', 'Комплекс услуг зернового терминала ООО «УкрТрансАгро» включает в себя широкий спектр опций, позволяющих получить комплекc качественных услуг.', 'Комплекс послуг зернового терміналу ТОВ «Укртрансагро» включає в себе широкий спектр опцій, що дозволяють отримати комплекc якісних послуг.', 'The scope of services of the grain terminal Ltd. \"Ukrtransagro\" includes a wide range of options to get a complex of high-quality services.', 'Мы оказываем комплекс услуг по приемке, накоплению, хранению и отгрузке на морские суда зерновых и масличных культур, а также полный спектр услуг зернового терминала.', 'Ми надаємо комплекс послуг з прийому, накопиченню, зберіганню та відвантаженню на морські судна зернових і олійних культур, а також повний спектр послуг  зернового терміналу.', 'We offer a range of services for acceptance, accumulation, storage and loading to the sea vessels of grain and oil-bearing crops, and also provide a full range of services of grain terminal.', '<p>Терминал полностью автоматизирован, оснащен оборудованием компании CHIEF (США), на терминале предусмотрена система очистки, сушки, активной вентиляции зерна и масличных культур, что обеспечивает полное сохранение качества.</p>\r\n                <p>Терминал имеет всю необходимую транспортную инфраструктуру и развязки. Железнодорожная станция «Мариуполь-порт» имеет достаточную пропускную способность до 300-400 вагонов в сутки. Терминал оборудован автомобильным приемом на два автомобиля, что позволяет одновременно осуществлять приемку различных культур разного качества. Приемник для железнодорожных вагонов способен осуществлять одновременную выгрузку 3-х вагонов.</p>\r\n                <p>Отгрузка зерновых и масличных культур может осуществляться как на морские суда, так и на автомобильный и железнодорожный транспорт. Возможны также различные прямые варианты погрузки судна: автомобиль-судно, ж/д вагон-судно.</p>\r\n\r\n\r\n', '<p>Термінал повністю автоматизований, оснащений обладнанням компанії CHIEF (США), на терміналі передбачена система очищення, сушки, активного вентилювання зерна і олійних культур, що забезпечує повне збереження якості.</p>\r\n                <p>Термінал має усю необхідну транспортну інфраструктуру і розв`язки. Залізнична станція «Маріуполь-порт» має достатню пропускну здатність до 300-400 вагонів на добу. Термінал обладнаний автомобільним прийомом на два автомобіля, що дозволяє одночасно здійснювати приймання різних культур різної якості. Приймач для залізничних вагонів здатний здійснювати одночасне вивантаження 3-х вагонів.</p>\r\n                <p>Відвантаження зернових і олійних культур може здійснюватися як на морські судна, так і на автомобільний та залізничний транспорт. Можливі також різні прямі варіанти навантаження судна: автомобіль-судно, ж/д вагон-судно .</p>\r\n', '<p>The grain terminal is fully automated and equipped with facilities delivered by CHIEF company (USA).</p>\r\n<p>The terminal provides cleaning, drying, active ventilation of grains and oil-bearing crops, ensuring the quality preservation of the received grain. The terminal has all the necessary transport infrastructure and traffic intersections.  Mariupol-Port railway station has a sufficient capacity up to 300-400 railcars per day. The grain terminal is equipped with receivers for two cargo vehicles, allowing simultaneous acceptance of different types of grains of a different quality. Receiver for railcars is capable of simultaneous unloading of 3 railcars.</p>\r\n<p>The shipment of grains and oil-bearing crops can be carried out to sea vessels, motor and railway transport. There are also various options for direct loading of the vessel: from truck to vessel, from railcar to vessel etc.</p>\r\n', '', 'yes'),
(15, 'charact', '', '', 'Характеристики терминала', 'Характеристики терміналу', 'Specifications terminal', 'Характеристики терминала', 'Характеристики терміналу', 'Technical specifications and capabilities of the grain terminal', '', '', '', 'Характеристики терминала', 'Характеристики терміналу', 'Technical specifications', 'ООО «УкрТрансАгро» - зерновой терминал в Мариупольском морском порту (Мариуполь, Украина). Емкость единовременного хранения в силосах 48,120 тыс. тонн, склады напольного хранения – 9500 тыс.тонн, мощность по перевалке до 1,0 млн. тонн в год. Введён в эксплуатацию в 2008 году.', 'ТОВ «УкрТрансАгро » - зерновий термінал вакваторії Азовського моря (Маріуполь, Україна) ємністю одноразового зберігання 48,120 тис. тонн і потужністю з перевалки до 1,0 млн. тонн на рік, введений в експлуатацію в 2008 році.', 'UkrTransAgro is a grain terminal with a capacity of 48.120 ths. tons for simultaneous storage and handling capacity up to 1.0 mln. tons per year situated in the Sea of Azov (Mariupol, Ukraine). Terminal was put into operation in 2008.', 'УкрТрансАгро обеспечивает полную количественную и качественную сохранность зерна, списание убыли в весе в соответствии с ГОСТами и другими нормативными актами и техническими регламентами, утвержденными в Украине.', 'УкрТрансАгро забезпечує повне кількісне і якісне збереження зерна, списання убутку у вазі у відповідності ДСТУ та іншими нормативними актами і технічними регламентами, затвердженими в Україні.', 'Full quantitative and qualitative safety of grain and weight loss write-offs in accordance with GOST and other statutory acts and technical regulations adopted in Ukraine are guaranteed.', '', '', '', '', 'yes'),
(16, 'client', '', '', 'Клиентам', 'Клієнтам', 'Client', 'Клиентам', 'Клієнтам', 'For Clients ', '', '', '', 'Клиентам', 'Клієнтам', 'Dear Customer!', 'ООО «УкрТрансАгро» постоянно предпринимает активные шаги для поддержания позиции максимально ориентированной на потребности клиентов.', 'ТОВ «УкрТрансАгро» постійно робить активні кроки для підтримки позиції, максимально орієнтованої на потреби клієнтів.', 'UkrTransAgro, LLC is constantly taking active steps to maintain the position of the entirely customer oriented company. ', 'Cпециалистами компании разработана версия личного кабинета клиента. Использование данного сервиса позволяет Вам, удаленно, через наш сайт:', 'З цією метою фахівцями компанії розробляється версія особистого кабінету клієнта. Використання даного сервісу в майбутньому дозволить Вам, віддалено, через наш сайт:', 'For this purpose our specialists develop a new service of Online Client‘s Account. In the future, you’ll be able to carry out the following actions via our website:', '<ul>\r\n                    <li>Отслеживать в разрезе культур и классов оперативную информацию о наличии груза (приход, отгрузка);</li>\r\n                    <li>Получить доступ к реестру на поступивший груз за любую дату (с указанием не только веса транспортной единицы, но и качественных показателей);</li>\r\n                    <li>Увидеть данные о заполнении емкостей;</li>\r\n                    <li>Получить справочную информацию о предлагаемых ООО «Укртрансагро» услугах и их стоимости;</li>\r\n                </ul>\r\n                <p><strong>И это еще далеко не весь перечень возможностей!</strong> Напишите нам о своих потребностях и мы примем их во внимание!</p>', '<ul>\r\n                    <li>Відстежувати в розрізі культур і класів оперативну інформацію про наявність вантажу (прихід, відвантаження);</li>\r\n                    <li>Отримати доступ до реєстру на вантаж, який надійшов, за будь-яку дату (із зазначенням не тільки ваги транспортної одиниці, а й якісних показників);</li>\r\n                    <li>Побачити дані про заповнення ємностей;</li>\r\n                    <li>Отримати довідкову інформацію про пропоновані ТОВ «Укртрансагро» послуги та їх вартість;</li>\r\n                </ul>\r\n                <p><strong>І це ще далеко не весь перелік можливостей! </strong> Напишіть нам про свої потреби і ми приймемо їх до уваги!</p>\r\n\r\n', '<ul>\r\n<li> To track online status information on your cargo (acceptance, shipment etc.);</li>\r\n<li>To access the registry of incoming cargo on any date (with indication of transport unit weight and quality indicators);</li>\r\n<li>To get the data on filling of capacities;</li>\r\n<li> To receive reference information about services offered by UkrTransAgro and the price list.</li>\r\n</ul>\r\n<p><strong>More options to come! I</strong> nform  us of  your needs and we shall take care of this!! </p>', '', 'yes'),
(17, 'news', '', '', 'Новости компании', 'Новини компании', 'Company news', 'Новости компании', 'Новини компанiї', 'Company news', '', '', '', 'Новости компании', 'Новини компанiї', 'Company news', 'Будьте в курсе последних новостей нашего терминала!', 'Будьте в курсі останніх новин нашого терміналу!', 'Do keep up-to-date of our terminal‘s latest news.', 'Мы всегда открыты для общения. Если Вы хотите узнать больше о ООО \"УкрТрансАгро\" обратитесь в нашу пресс-службу <a href=\"mailto:press@ukrtransagro.com\">press@ukrtransagro.com</a>  или посетите <a href=\"https://www.facebook.com/UkrTransAgro\" target=\"_blank\"> нашу страницу в сети Facebook </a>.', 'Ми завжди відкриті для спілкування. Якщо Ви хочете дізнатися більше про ТОВ \"Укртрансагро\" зверніться до нашої прес-служби <a href=\"mailto:press@ukrtransagro.com\">press@ukrtransagro.com</a>  або  відвідайте <a href=\"https://www.facebook.com/UkrTransAgro\" target=\"_blank\"> нашу сторінку у мережі Facebook </a>.', 'We are always open for communication. If you like to know more about “UkrTransAgro” please contact our press-service:  <a href=\"mailto:press@ukrtransagro.com\">press@ukrtransagro.com</a>  or visit <a href=\"https://www.facebook.com/UkrTransAgro\" target=\"_blank\">  our page in  Facebook</a>  .', '', '', '', '', 'yes'),
(18, 'gallery', '', '', 'Галерея', 'Галерея', 'Gallery', 'Галерея', 'Галерея', 'Gallery', '', '', '', 'Галерея', 'Галерея', 'Gallery', '', '', '', '', '', '', '', '', '', '', 'yes'),
(19, 'contacts', '', '', 'Контакты', 'Контакти', 'Contacts', 'Контакты', 'Контакти', 'Contacts', '', '', '', 'Контакты', 'Контакти', 'Contacts', '', '', '', '', '', '', '', '', '', '', 'yes');

